import { useState } from "react";
function App() {
  const [toDo, setToDo] = useState("");
  const onChange = (e) => setToDo(e.target.value);
  console.log(toDo);
  return (
    <div>
      <form>
        <input
          onChange={onChange}
          value={toDo}
          type="text"
          placeholder="write your todo"
        />
        <button>Add To Do</button>
      </form>
    </div>
  );
}

export default App;
