import { useEffect, useState } from "react";
import styles from "./button.module.css";

function App() {
  const [loading, setLoading] = useState(true);
  const [coins, setCoins] = useState([]); //초기값은 빈 배열
  const [krw_usd, setKRWex] = useState(0); //원화환율, 1원당 달러비율
  const [mode, setMode] = useState(true);

  //코인 시세 받아오기
  useEffect(() => {
    fetch("https://api.coinpaprika.com/v1/tickers") //api가연결되면네트워크 응답에서 가격정보를 받아옴
      .then((response) => response.json()) //json형태로변환
      .then((json) => {
        // console.log(json);
        setCoins(json);
      }); //json형태로 변환한걸 setCoins로 coins배열을 대체
  }, []);

  //환율정보 받아오기
  useEffect(() => {
    fetch(" https://api.manana.kr/exchange/price/KRW/1/USD.json")
      .then((response) => response.json())
      .then((json) => {
        console.log(json);
        setKRWex(json);
        setLoading(false);
      });
  }, []);

  //onExchange
  const onExChange = () => {
    if (mode) {
      setMode(false);
    } else {
      setMode(true);
    }
  };

  const article = () => {
    if (mode) {
      return coins.map((coin, index) => (
        <option key={index}>
          {coin.name} ({coin.symbol}):${coin.quotes.USD.price}
        </option>
      ));
    } else {
      return coins.map((coin, index) => (
        <option key={index}>
          {coin.name} ({coin.symbol}):${coin.quotes.USD.price / krw_usd}
        </option>
      ));
    }
  };
  console.log(article);
  return (
    <div>
      <h1>The Coins! {loading ? null : `(${coins.length})`}</h1>
      <div>
        <button onClick={onExChange}>{mode ? "USD" : "KRW"}</button>
      </div>
      <strong>{loading ? "Loading..." : null}</strong>
      <select>{article}</select>
    </div>
  );
}

export default App;
