import { useState } from "react";
function App() {
  const [toDo, setToDo] = useState(""); //toDo라는 state를 수정하는 setToDo함수설정
  const [toDos, setToDos] = useState([]); //toDos 수정하는 setToDos 함수 설정
  const onChange = (e) => {
    setToDo(e.target.value); //현재 input에 들어있는 값을 setToDo함수로 state변경
  };
  const onSubmit = (e) => {
    e.preventDefault(); //초기화방지

    if (toDo === "") {
      return;
    } //toDo가 비어있다면 실행안한다.

    setToDos((currentArray) => [toDo, ...currentArray]); //현재 toDos 배열에 들어와있는게 currentArray인데, toDo로 현재
    console.log(toDos);
    setToDo("");
  };
  return (
    <div>
      <form onSubmit={onSubmit}>
        <input
          onChange={onChange}
          value={toDo}
          type="text"
          placeholder="write your todo"
        />
        <button>Add To Do</button>
      </form>
    </div>
  );
}

export default App;
